package com.example.sbuxapp

import android.os.Bundle
import androidx.activity.ComponentActivity
import androidx.activity.compose.setContent
import androidx.compose.foundation.Image
import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.layout.ContentScale
import androidx.compose.ui.res.painterResource
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.LocalCafe
import androidx.compose.material.icons.filled.Remove
import androidx.compose.material.icons.filled.Add
import androidx.compose.material.icons.filled.Home
import androidx.compose.material.icons.filled.ShoppingCart
import androidx.compose.material.icons.filled.Person
import androidx.compose.ui.draw.clip
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material.icons.filled.AccountCircle
import androidx.compose.runtime.Composable
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import java.text.NumberFormat
import java.util.Locale
import androidx.compose.material.icons.filled.Edit
import androidx.compose.material.icons.filled.History
import androidx.compose.material.icons.filled.Settings
import androidx.compose.material.icons.filled.ExitToApp
import androidx.compose.foundation.clickable


data class DrinkItem(val id: Int, val title: String, val details: String, val price: Double, val imgRes: Int)
data class OrderEntry(val item: DrinkItem, var count: Int = 1)

val sampleDrinks = listOf(
 DrinkItem(1, "Caramel Macchiato", "Rich espresso with caramel and steamed milk", 61.0, R.drawable.caramel_macchiato),
 DrinkItem(2, "Caffè Latte", "Smooth blend of espresso and steamed milk", 48.0, R.drawable.caffe_latte),
 DrinkItem(3, "Americano", "Espresso shots topped with hot water", 38.0, R.drawable.americano),
 DrinkItem(4, "Caffè Mocha", "Espresso with bittersweet mocha and steamed milk", 57.0, R.drawable.caffe_mocha)
)

class MainActivity : ComponentActivity() {
 override fun onCreate(savedInstanceState: Bundle?) {
 super.onCreate(savedInstanceState)
 setContent {
 CoffeeHouseApp()
 }
 }
}

@Composable
fun CoffeeHouseApp() {
 var showIntro by remember { mutableStateOf(true) }
 var currentTab by remember { mutableStateOf(0) }
 val cart = remember { mutableStateListOf<OrderEntry>() }

 if (showIntro) {
 SplashScreen { showIntro = false }
 } else {
 Scaffold(
 bottomBar = {
 NavigationBar {
 val tabs = listOf("Beranda", "Minuman", "Pesanan", "Akun")
 val icons = listOf(Icons.Default.Home, Icons.Default.LocalCafe, Icons.Default.ShoppingCart, Icons.Default.Person)
 tabs.forEachIndexed { index, label ->
 NavigationBarItem(
 selected = currentTab == index,
 onClick = { currentTab = index },
 icon = { Icon(icons[index], contentDescription = label) },
 label = { Text(label) }
 )
 }
 }
 }
 ) { innerPadding ->
 Box(modifier = Modifier.padding(innerPadding)) {
 when (currentTab) {
 0 -> WelcomeScreen()
 1 -> DrinkMenuScreen(cart)
 2 -> CartPage(cart)
 3 -> UserProfileScreen()
 }
 }
 }
 }
}

@Composable
fun SplashScreen(onContinue: () -> Unit) {
 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(Color(0xFF00704A)), // Warna hijau khas Starbucks
 contentAlignment = Alignment.Center
 ) {
 Column(horizontalAlignment = Alignment.CenterHorizontally) {
 // Gambar Logo Starbucks
 Image(
 painter = painterResource(id = R.drawable.logo_starbucks),
 contentDescription = "Starbucks Logo",
 modifier = Modifier.size(120.dp)
 )

 Spacer(modifier = Modifier.height(24.dp))

 // "Welcome to"
 Text(
 "Welcome to",
 color = Color.White,
 fontSize = 22.sp,
 fontWeight = FontWeight.Medium
 )

 // "Starbucks"
 Text(
 "Starbucks",
 color = Color.White,
 fontSize = 32.sp,
 fontWeight = FontWeight.Bold
 )

 Spacer(modifier = Modifier.height(12.dp))

 // Deskripsi tambahan
 Text(
 "Thanks for downloading,\nthe new Starbucks ID App!",
 color = Color.White,
 fontSize = 18.sp,
 textAlign = TextAlign.Center
 )

 Spacer(modifier = Modifier.height(32.dp))

 // Tombol "Mulai"
 Button(
 onClick = onContinue,
 colors = ButtonDefaults.buttonColors(containerColor = Color.White)
 ) {
 Text("Start", color = Color.Black, fontSize = 18.sp)
 }
 }
 }
}



@Composable
fun WelcomeScreen() {
 LazyColumn(
 modifier = Modifier
 .fillMaxSize()
 .padding(horizontal = 16.dp, vertical = 24.dp)
 ) {
 item {
 // Sapaan di atas
 Text(
 text = "Good Morning, Fahmi",
 style = MaterialTheme.typography.headlineSmall,
 fontWeight = FontWeight.SemiBold
 )
 Text(
 text = "It's a great day for coffee!",
 style = MaterialTheme.typography.bodyLarge,
 color = Color.Gray,
 modifier = Modifier.padding(top = 4.dp, bottom = 16.dp)
 )
 }

 item {
 // Gambar banner promosi
 Image(
 painter = painterResource(id = R.drawable.banner_promo),
 contentDescription = "Promo Banner",
 contentScale = ContentScale.Crop,
 modifier = Modifier
 .fillMaxWidth()
 .height(180.dp)
 .clip(RoundedCornerShape(16.dp))
 )
 Spacer(modifier = Modifier.height(24.dp))
 }

 item {
 Text(
 text = "✨ Menu Rekomendasi buat kamu",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold
 )
 Spacer(modifier = Modifier.height(8.dp))
 }

 items(sampleDrinks) {
 DrinkCard(it)
 }
 }
}

@Composable
fun DrinkCard(item: DrinkItem) {
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 8.dp),
 shape = RoundedCornerShape(16.dp),
 elevation = CardDefaults.cardElevation(defaultElevation = 6.dp)
 ) {
 Row(
 modifier = Modifier
 .padding(12.dp)
 .fillMaxWidth(),
 verticalAlignment = Alignment.CenterVertically
 ) {
 Image(
 painter = painterResource(id = item.imgRes),
 contentDescription = item.title,
 modifier = Modifier
 .size(72.dp)
 .clip(RoundedCornerShape(12.dp)),
 contentScale = ContentScale.Crop
 )
 Spacer(modifier = Modifier.width(16.dp))
 Column(modifier = Modifier.weight(1f)) {
 Text(
 text = item.title,
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.SemiBold
 )
 Spacer(modifier = Modifier.height(4.dp))
 Text(
 text = item.details,
 style = MaterialTheme.typography.bodyMedium,
 color = Color.Gray
 )
 }
 }
 }
}


@Composable
fun DrinkMenuScreen(cart: MutableList<OrderEntry>) {
 val formatter = NumberFormat.getNumberInstance(Locale("in", "ID"))

 LazyColumn(modifier = Modifier.padding(16.dp)) {
 items(sampleDrinks) { drink ->
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 10.dp),
 shape = RoundedCornerShape(14.dp),
 elevation = CardDefaults.cardElevation(6.dp)
 ) {
 Row(
 modifier = Modifier
 .padding(16.dp)
 .fillMaxWidth(),
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.SpaceBetween
 ) {
 Image(
 painter = painterResource(drink.imgRes),
 contentDescription = drink.title,
 modifier = Modifier
 .size(72.dp)
 .padding(end = 16.dp),
 contentScale = ContentScale.Crop
 )

 Column(modifier = Modifier.weight(1f)) {
 Text(
 text = drink.title,
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold
 )
 Spacer(modifier = Modifier.height(4.dp))
 Text(
 text = drink.details,
 style = MaterialTheme.typography.bodySmall,
 color = Color.Gray
 )
 Spacer(modifier = Modifier.height(6.dp))
 Text(
 text = "Rp${formatter.format((drink.price * 1000).toInt())}",
 style = MaterialTheme.typography.bodyMedium,
 fontWeight = FontWeight.Medium,
 color = Color(0xFF00704A)
 )
 }

 Row(verticalAlignment = Alignment.CenterVertically) {
 IconButton(onClick = {
 val item = cart.find { it.item.id == drink.id }
 if (item != null && item.count > 0) item.count--
 }) {
 Icon(Icons.Default.Remove, contentDescription = "Kurangi")
 }

 Text(
 text = cart.find { it.item.id == drink.id }?.count?.toString() ?: "0",
 fontSize = 16.sp,
 modifier = Modifier.padding(horizontal = 6.dp)
 )

 IconButton(onClick = {
 val item = cart.find { it.item.id == drink.id }
 if (item != null) item.count++ else cart.add(OrderEntry(drink, 1))
 }) {
 Icon(Icons.Default.Add, contentDescription = "Tambah")
 }
 }
 }
 }
 }
 }
}


@Composable
fun CartPage(cart: List<OrderEntry>) {
 val total = cart.sumOf { it.item.price * it.count }
 val formatter = NumberFormat.getNumberInstance(Locale("in", "ID"))

 Column(modifier = Modifier.padding(16.dp)) {
 Text(
 "Pesanan Anda",
 style = MaterialTheme.typography.headlineSmall,
 fontWeight = FontWeight.Bold
 )
 Spacer(modifier = Modifier.height(16.dp))

 cart.forEach {
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 8.dp),
 elevation = CardDefaults.cardElevation(6.dp)
 ) {
 Row(
 modifier = Modifier
 .padding(16.dp)
 .fillMaxWidth(),
 verticalAlignment = Alignment.CenterVertically
 ) {
 Image(
 painter = painterResource(id = it.item.imgRes),
 contentDescription = it.item.title,
 modifier = Modifier
 .size(70.dp)
 .padding(end = 16.dp),
 contentScale = ContentScale.Crop
 )

 Column(modifier = Modifier.weight(1f)) {
 Text(
 text = it.item.title,
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold
 )
 Spacer(modifier = Modifier.height(4.dp))
 Text(
 text = it.item.details,
 style = MaterialTheme.typography.bodyMedium,
 color = Color.Gray
 )
 Spacer(modifier = Modifier.height(6.dp))
 Text("Jumlah: ${it.count}", style = MaterialTheme.typography.bodySmall)
 }

 Spacer(modifier = Modifier.width(12.dp))

 Text(
 "Rp${formatter.format((it.item.price * 1000 * it.count).toInt())}",
 style = MaterialTheme.typography.bodyLarge,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.align(Alignment.Bottom)
 )
 }
 }
 }

 Spacer(modifier = Modifier.height(24.dp))
 Text(
 "Total: Rp${formatter.format((total * 1000).toInt())}",
 style = MaterialTheme.typography.titleLarge,
 fontWeight = FontWeight.Bold
 )
 Spacer(modifier = Modifier.height(16.dp))
 Button(
 onClick = { /* aksi checkout */ },
 modifier = Modifier
 .fillMaxWidth()
 .height(52.dp),
 colors = ButtonDefaults.buttonColors(containerColor = Color(0xFF00704A))
 ) {
 Text("Pesan Sekarang", color = Color.White, fontSize = 16.sp)
 }
 }
}

