@Composable
fun CoffeeHouseApp() {
 var currentTab by remember { mutableStateOf(0) }
 val cart = remember { mutableStateListOf<OrderEntry>() }
 Scaffold(
 bottomBar = {
 NavigationBar {
 val tabs = listOf("Home", "Menu", "Order", "Account")
 val icons = listOf(Icons.Default.Home, Icons.Default.LocalCafe, Icons.Default.ShoppingCart, Icons.Default.Person)
 tabs.forEachIndexed { index, label ->
 NavigationBarItem(
 selected = currentTab == index,
 onClick = { currentTab = index },
 icon = { Icon(icons[index], contentDescription = label) },
 label = { Text(label) }
 )
 }
 }
 }
 ) { innerPadding ->
 Box(modifier = Modifier.padding(innerPadding)) {
 when (currentTab) {
 0 -> WelcomeScreen()
 1 -> DrinkMenuScreen(cart)
 2 -> CartPage(cart)
 3 -> UserProfileScreen()
 }
 }
 }
 }

