)


class MainActivity : ComponentActivity() {
 override fun onCreate(savedInstanceState: Bundle?) {
 super.onCreate(savedInstanceState)
 setContent {
 CoffeeHouseApp()
 }
 }
}

@Composable
fun CoffeeHouseApp() {
 var currentTab by remember { mutableStateOf(0) }
 val cart = remember { mutableStateListOf<OrderEntry>() }
 Scaffold(
 bottomBar = {
 NavigationBar {
 val tabs = listOf("Home", "Menu", "Order", "Account")
 val icons = listOf(
 Icons.Default.Home,
 Icons.Default.LocalCafe,
 Icons.Default.ShoppingCart,
 Icons.Default.Person
 )
 tabs.forEachIndexed { index, label ->
 NavigationBarItem(
 selected = currentTab == index,
 onClick = { currentTab = index },
 icon = { Icon(icons[index], contentDescription = label) },
 label = { Text(label) }
 )
 }
 }
 }
 ) { innerPadding ->
 Box(modifier = Modifier.padding(innerPadding)) {
 when (currentTab) {
 0 -> WelcomeScreen()
 1 -> DrinkMenuScreen(cart)
 2 -> CartPage(cart)
 3 -> UserProfileScreen()
 }
 }
 }
}

@Composable
fun SplashScreen(onContinue: () -> Unit) {
 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(Color(0xFF00704A)), // Warna hijau khas Starbucks
 contentAlignment = Alignment.Center
 ) {
 Column(horizontalAlignment = Alignment.CenterHorizontally) {
 // Gambar Logo Starbucks
 Image(
 painter = painterResource(id = R.drawable.logo_starbucks),
 contentDescription = "Starbucks Logo",
 modifier = Modifier.size(120.dp)
 )

 Spacer(modifier = Modifier.height(24.dp))

 // "Welcome to"
 Text(
 "Welcome to",
 color = Color.White,
 fontSize = 22.sp,
 fontWeight = FontWeight.Medium
 )

 // "Starbucks"
 Text(
 "Starbucks",
 color = Color.White,
 fontSize = 32.sp,
 fontWeight = FontWeight.Bold
 )

 Spacer(modifier = Modifier.height(12.dp))

 // Deskripsi tambahan
 Text(
 "Thanks for downloading,\nthe new Starbucks ID App!",
 color = Color.White,
 fontSize = 18.sp,
 textAlign = TextAlign.Center
 )

 Spacer(modifier = Modifier.height(32.dp))

 // Tombol "Mulai"
 Button(
 onClick = onContinue,
 colors = ButtonDefaults.buttonColors(containerColor = Color.White)
 ) {
 Text("Start", color = Color.Black, fontSize = 18.sp)
 }
 }
 }
}

