@Composable
fun DrinkMenuScreen(cart: MutableList<OrderEntry>) {
 var selectedCategory by remember { mutableStateOf("All") }
 val categories = remember { menuCategories }

 LazyColumn(
 modifier = Modifier
 .fillMaxSize()
 .padding(16.dp)
 ) {
 item {
 // Menu Title
 Text(
 text = "Menu",
 style = MaterialTheme.typography.headlineSmall,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 16.dp)
 )
 }

 item {
 // Search Bar
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(bottom = 16.dp),
 shape = RoundedCornerShape(12.dp),
 colors = CardDefaults.cardColors(containerColor = Color(0xFFF5F5F5))
 ) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp, vertical = 12.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 Icon(
 painter = painterResource(android.R.drawable.ic_menu_search),
 contentDescription = "Search",
 tint = Color.Gray,
 modifier = Modifier.size(20.dp)
 )
 Spacer(modifier = Modifier.width(12.dp))
 Text(
 text = "Search Menu",
 color = Color.Gray,
 fontSize = 16.sp
 )
 }
 }
 }

 item {
 // Category Chips
 LazyRow(
 horizontalArrangement = Arrangement.spacedBy(8.dp),
 modifier = Modifier.padding(bottom = 24.dp)
 ) {
 items(categories) { category ->
 CategoryChip(
 category = category,
 isSelected = category.name == selectedCategory,
 onClick = { selectedCategory = category.name }
 )
 }
 }
 }

 // Coffee Section
 if (selectedCategory == "Coffee" || selectedCategory == "All") {
 item {
 Text(
 text = "Coffee",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 12.dp)
 )

 LazyRow(
 horizontalArrangement = Arrangement.spacedBy(12.dp),
 modifier = Modifier.padding(bottom = 24.dp)
 ) {
 items(coffeeDrinks) { drink ->
 MenuDrinkCard(drink, cart)
 }
 }
 }
 }

 // Tea Section
 if (selectedCategory == "Tea" || selectedCategory == "All") {
 item {
 Text(
 text = "Tea",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 12.dp)
 )

 LazyRow(
 horizontalArrangement = Arrangement.spacedBy(12.dp),
 modifier = Modifier.padding(bottom = 24.dp)
 ) {
 items(teaDrinks) { drink ->
 MenuDrinkCard(drink, cart)
 }
 }
 }
 }

 // Frappuccino Section
 if (selectedCategory == "Frappuccino" || selectedCategory == "All") {
 item {
 Text(
 text = "Frappuccino",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 12.dp)
 )

 LazyRow(
 horizontalArrangement = Arrangement.spacedBy(12.dp),
 modifier = Modifier.padding(bottom = 24.dp)
 ) {
 items(frappuccinoDrinks) { drink ->
 MenuDrinkCard(drink, cart)
 }
 }
 }
 }

 // Food Section
 if (selectedCategory == "Food" || selectedCategory == "All") {
 item {
 Text(
 text = "Food",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 12.dp)
 )

 LazyRow(
 horizontalArrangement = Arrangement.spacedBy(12.dp),
 modifier = Modifier.padding(bottom = 24.dp)
 ) {
 items(foodItems) { drink ->
 MenuDrinkCard(drink, cart)
 }
 }
 }
 }
 }
}

@Composable
fun CategoryChip(
 category: CategoryItem,
 isSelected: Boolean,
 onClick: () -> Unit
) {
 Card(
 modifier = Modifier
 .clickable { onClick() },
 shape = RoundedCornerShape(20.dp),
 colors = CardDefaults.cardColors(
 containerColor = if (isSelected) Color(0xFF00704A) else Color(0xFFF5F5F5)
 )
 ) {
 Text(
 text = category.name,
 modifier = Modifier.padding(horizontal = 16.dp, vertical = 8.dp),
 color = if (isSelected) Color.White else Color.Black,
 fontSize = 14.sp,
 fontWeight = if (isSelected) FontWeight.Medium else FontWeight.Normal
 )
 }
}

@Composable
fun MenuDrinkCard(item: DrinkItem, cart: MutableList<OrderEntry>) {
 val formatter = NumberFormat.getNumberInstance(Locale("in", "ID"))

 Card(
 modifier = Modifier
 .width(140.dp)
 .height(180.dp),
 shape = RoundedCornerShape(12.dp),
 elevation = CardDefaults.cardElevation(defaultElevation = 4.dp),
 colors = CardDefaults.cardColors(containerColor = Color.White)
 ) {
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(12.dp)
 ) {
 // Drink Image
 Image(
 painter = painterResource(id = item.imgRes),
 contentDescription = item.title,
 modifier = Modifier
 .fillMaxWidth()
 .height(80.dp)
 .clip(RoundedCornerShape(8.dp)),
 contentScale = ContentScale.Crop
 )

 Spacer(modifier = Modifier.height(8.dp))

 Text(
 text = item.title,
 style = MaterialTheme.typography.bodyMedium,
 fontWeight = FontWeight.Medium,
 maxLines = 1
 )

 Spacer(modifier = Modifier.height(4.dp))

 Text(
 text = "Rp${formatter.format((item.price * 1000).toInt())}",
 style = MaterialTheme.typography.bodySmall,
 color = Color(0xFF00704A),
 fontWeight = FontWeight.Bold
 )

 Spacer(modifier = Modifier.weight(1f))

 // Add to cart button (optional)
 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.End,
 verticalAlignment = Alignment.CenterVertically
 ) {
 val itemInCart = cart.find { it.item.id == item.id }
 if (itemInCart != null && itemInCart.count > 0) {
 Text(
 text = itemInCart.count.toString(),
 fontSize = 12.sp,
 modifier = Modifier.padding(end = 4.dp)
 )
 }

 Card(
 modifier = Modifier
 .size(24.dp)
 .clickable {
 val existingItem = cart.find { it.item.id == item.id }
 if (existingItem != null) {
 existingItem.count++
 } else {
 cart.add(OrderEntry(item, 1))
 }
 },
 shape = RoundedCornerShape(4.dp),
 colors = CardDefaults.cardColors(containerColor = Color(0xFF00704A))
 ) {
 Box(
 modifier = Modifier.fillMaxSize(),
 contentAlignment = Alignment.Center
 ) {
 Icon(
 imageVector = Icons.Default.Add,
 contentDescription = "Add",
 tint = Color.White,
 modifier = Modifier.size(16.dp)
 )
 }
 }
 }
 }
 }
}

@Composable
fun CartPage(cart: List<OrderEntry>) {
 val total = cart.sumOf { it.item.price * it.count }
 val formatter = NumberFormat.getNumberInstance(Locale("in", "ID"))

 LazyColumn(
 modifier = Modifier
 .fillMaxSize()
 .padding(16.dp)
 ) {
 item {
 // Header with cart icon
 Row(
 verticalAlignment = Alignment.CenterVertically,
 modifier = Modifier.padding(bottom = 24.dp)
 ) {
 Icon(
 imageVector = Icons.Default.ShoppingCart,
 contentDescription = "Shopping Cart",
 tint = Color(0xFF00704A),
 modifier = Modifier.size(28.dp)
 )
 Spacer(modifier = Modifier.width(12.dp))
 Text(
 "My Order",
 style = MaterialTheme.typography.headlineSmall,
 fontWeight = FontWeight.Bold
 )
 }
 }

 if (cart.isEmpty()) {
 item {
 // Empty cart state
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 60.dp),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Icon(
 imageVector = Icons.Default.ShoppingCart,
 contentDescription = "Empty Cart",
 tint = Color.Gray,
 modifier = Modifier.size(80.dp)
 )
 Spacer(modifier = Modifier.height(16.dp))
 Text(
 "Your cart is empty",
 style = MaterialTheme.typography.titleMedium,
 color = Color.Gray
 )
 Text(
 "Add some delicious items to get started!",
 style = MaterialTheme.typography.bodyMedium,
 color = Color.Gray,
 textAlign = TextAlign.Center
 )
 }
 }
 } else {
 items(cart) { orderEntry ->
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 6.dp),
 shape = RoundedCornerShape(16.dp),
 elevation = CardDefaults.cardElevation(4.dp),
 colors = CardDefaults.cardColors(containerColor = Color.White)
 ) {
 Row(
 modifier = Modifier.padding(16.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 // Item Image
 Card(
 shape = RoundedCornerShape(12.dp),
 modifier = Modifier.size(60.dp)
 ) {
 Image(
 painter = painterResource(id = orderEntry.item.imgRes),
 contentDescription = orderEntry.item.title,
 modifier = Modifier.fillMaxSize(),
 contentScale = ContentScale.Crop
 )
 }

 Spacer(modifier = Modifier.width(16.dp))

 // Item Details
 Column(modifier = Modifier.weight(1f)) {
 Text(
 text = orderEntry.item.title,
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold
 )
 Spacer(modifier = Modifier.height(4.dp))
 Text(
 text = "Qty: ${orderEntry.count}",
 style = MaterialTheme.typography.bodySmall,
 color = Color.Gray
 )
 }

 // Price
 Column(horizontalAlignment = Alignment.End) {
 Text(
 text = "Rp${formatter.format((orderEntry.item.price * 1000).toInt())}",
 style = MaterialTheme.typography.bodySmall,
 color = Color.Gray
 )
 Text(
 text = "Rp${formatter.format((orderEntry.item.price * 1000 * orderEntry.count).toInt())}",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold,
 color = Color(0xFF00704A)
 )
 }
 }
 }
 }

 item {
 Spacer(modifier = Modifier.height(24.dp))

 // Order Summary
 Card(
 modifier = Modifier.fillMaxWidth(),
 shape = RoundedCornerShape(16.dp),
 colors = CardDefaults.cardColors(containerColor = Color(0xFFF8F9FA))
 ) {
 Column(modifier = Modifier.padding(20.dp)) {
 Text(
 "Order Summary",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 12.dp)
 )

 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.SpaceBetween
 ) {
 Text(
 "Subtotal (${cart.sumOf { it.count }} items)",
 style = MaterialTheme.typography.bodyMedium
 )
 Text(
 "Rp${formatter.format((total * 1000).toInt())}",
 style = MaterialTheme.typography.bodyMedium,
 fontWeight = FontWeight.Medium
 )
 }

 Spacer(modifier = Modifier.height(8.dp))

 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.SpaceBetween
 ) {
 Text(
 "Tax & Fees",
 style = MaterialTheme.typography.bodyMedium
 )
 Text(
 "Rp${formatter.format((total * 100).toInt())}",
 style = MaterialTheme.typography.bodyMedium,
 fontWeight = FontWeight.Medium
 )
 }

 Spacer(modifier = Modifier.height(12.dp))

 // Divider line
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .height(1.dp)
 .background(Color.Gray.copy(alpha = 0.3f))
 )

 Spacer(modifier = Modifier.height(12.dp))

 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.SpaceBetween
 ) {
 Text(
 "Total",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold
 )
 Text(
 "Rp${formatter.format(((total * 1000) + (total * 100)).toInt())}",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold,
 color = Color(0xFF00704A)
 )
 }
 }
 }

 Spacer(modifier = Modifier.height(20.dp))

 // Order Button
 Button(
 onClick = { /* checkout action */ },
 modifier = Modifier
 .fillMaxWidth()
 .height(56.dp),
 colors = ButtonDefaults.buttonColors(containerColor = Color(0xFF00704A)),
 shape = RoundedCornerShape(16.dp)
 ) {
 Icon(
 imageVector = Icons.Default.ShoppingCart,
 contentDescription = null,
 tint = Color.White,
 modifier = Modifier.size(20.dp)
 )
 Spacer(modifier = Modifier.width(8.dp))
 Text(
 "Place Order",
 color = Color.White,
 fontSize = 16.sp,
 fontWeight = FontWeight.Bold
 )
 }

 Spacer(modifier = Modifier.height(16.dp))
 }
 }
 }
}

