@Composable
fun MenuSearchResultCard(item: DrinkItem, cart: MutableList<OrderEntry>) {
 val formatter = NumberFormat.getNumberInstance(Locale("in", "ID"))

 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 4.dp),
 shape = RoundedCornerShape(12.dp),
 elevation = CardDefaults.cardElevation(defaultElevation = 2.dp),
 colors = CardDefaults.cardColors(containerColor = Color.White)
 ) {
 Row(
 modifier = Modifier
 .padding(16.dp)
 .fillMaxWidth(),
 verticalAlignment = Alignment.CenterVertically
 ) {
 // Item Image
 Card(
 shape = RoundedCornerShape(8.dp),
 modifier = Modifier.size(60.dp)
 ) {
 Image(
 painter = painterResource(id = item.imgRes),
 contentDescription = item.title,
 modifier = Modifier.fillMaxSize(),
 contentScale = ContentScale.Crop
 )
 }

 Spacer(modifier = Modifier.width(16.dp))

 // Item Details
 Column(modifier = Modifier.weight(1f)) {
 Text(
 text = item.title,
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold
 )
 Spacer(modifier = Modifier.height(4.dp))
 Text(
 text = item.details,
 style = MaterialTheme.typography.bodySmall,
 color = Color.Gray,
 maxLines = 2
 )
 Spacer(modifier = Modifier.height(8.dp))
 Text(
 text = "Rp${formatter.format((item.price * 1000).toInt())}",
 style = MaterialTheme.typography.bodyMedium,
 fontWeight = FontWeight.Bold,
 color = Color(0xFF00704A)
 )
 }

 Spacer(modifier = Modifier.width(16.dp))

 // Add to Cart Button
 Row(
 verticalAlignment = Alignment.CenterVertically
 ) {
 val itemInCart = cart.find { it.item.id == item.id }
 if (itemInCart != null && itemInCart.count > 0) {
 Text(
 text = itemInCart.count.toString(),
 fontSize = 14.sp,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(end = 8.dp)
 )
 }

 Card(
 modifier = Modifier
 .size(32.dp)
 .clickable {
 val existingItem = cart.find { it.item.id == item.id }
 if (existingItem != null) {
 existingItem.count++
 } else {
 cart.add(OrderEntry(item, 1))
 }
 },
 shape = RoundedCornerShape(8.dp),
 colors = CardDefaults.cardColors(containerColor = Color(0xFF00704A))
 ) {
 Box(
 modifier = Modifier.fillMaxSize(),
 contentAlignment = Alignment.Center
 ) {
 Icon(
 imageVector = Icons.Default.Add,
 contentDescription = "Add to Cart",
 tint = Color.White,
 modifier = Modifier.size(20.dp)
 )
 }
 }
 }
 }
 }
}

@Composable
fun CoffeeHouseApp() {
 var currentTab by remember { mutableStateOf(0) }
 val cart = remember { mutableStateListOf<OrderEntry>() }
 Scaffold(
 bottomBar = {
 NavigationBar {
 val tabs = listOf("Home", "Menu", "Order", "Account")
 val icons = listOf(
 Icons.Default.Home,
 Icons.Default.LocalCafe,
 Icons.Default.ShoppingCart,
 Icons.Default.Person
 )
 tabs.forEachIndexed { index, label ->
 NavigationBarItem(
 selected = currentTab == index,
 onClick = { currentTab = index },
 icon = { Icon(icons[index], contentDescription = label) },
 label = { Text(label) }
 )
 }
 }
 }
 ) { innerPadding ->
 Box(modifier = Modifier.padding(innerPadding)) {
 when (currentTab) {
 0 -> WelcomeScreen()
 1 -> DrinkMenuScreen(cart)
 2 -> CartPage(cart)
 3 -> UserProfileScreen()
 }
 }
 }
}

@Composable
fun SplashScreen(onContinue: () -> Unit) {
 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(Color(0xFF00704A)), // Warna hijau khas Starbucks
 contentAlignment = Alignment.Center
 ) {
 Column(horizontalAlignment = Alignment.CenterHorizontally) {
 // Gambar Logo Starbucks
 Image(
 painter = painterResource(id = R.drawable.logo_starbucks),
 contentDescription = "Starbucks Logo",
 modifier = Modifier.size(120.dp)
 )

 Spacer(modifier = Modifier.height(24.dp))

 // "Welcome to"
 Text(
 "Welcome to",
 color = Color.White,
 fontSize = 22.sp,
 fontWeight = FontWeight.Medium
 )

 // "Starbucks"
 Text(
 "Starbucks",
 color = Color.White,
 fontSize = 32.sp,
 fontWeight = FontWeight.Bold
 )

 Spacer(modifier = Modifier.height(12.dp))

 // Deskripsi tambahan
 Text(
 "Thanks for downloading,\nthe new Starbucks ID App!",
 color = Color.White,
 fontSize = 18.sp,
 textAlign = TextAlign.Center
 )

 Spacer(modifier = Modifier.height(32.dp))

 // Tombol "Mulai"
 Button(
 onClick = onContinue,
 colors = ButtonDefaults.buttonColors(containerColor = Color.White)
 ) {
 Text("Start", color = Color.Black, fontSize = 18.sp)
 }
 }
 }
}

@Composable
fun WelcomeScreen() {
 var searchQuery by remember { mutableStateOf("") }
 var isSearchActive by remember { mutableStateOf(false) }

 // Filter drinks based on search query
 val filteredCoffeeDrinks = if (searchQuery.isBlank()) {
 coffeeDrinks
 } else {
 coffeeDrinks.filter {
 it.title.contains(searchQuery, ignoreCase = true) ||
 it.details.contains(searchQuery, ignoreCase = true)
 }
 }

 LazyColumn(
 modifier = Modifier
 .fillMaxSize()
 .padding(horizontal = 16.dp, vertical = 8.dp)
 ) {
 if (!isSearchActive) {
 item {
 // Happy Mondays Banner
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(bottom = 16.dp),
 shape = RoundedCornerShape(16.dp),
 colors = CardDefaults.cardColors(containerColor = Color(0xFF00704A))
 ) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 Column(modifier = Modifier.weight(1f)) {
 Text(
 text = "★ STARBUCKS REWARDS",
 color = Color.White,
 fontSize = 12.sp,
 fontWeight = FontWeight.Medium
 )
 Text(
 text = "HAPPY",
 color = Color.White,
 fontSize = 24.sp,
 fontWeight = FontWeight.Bold
 )
 Text(
 text = "MONDAYS",
 color = Color.White,
 fontSize = 24.sp,
 fontWeight = FontWeight.Bold
 )
 Spacer(modifier = Modifier.height(8.dp))
 Text(
 text = "Get any Grande Frappuccino, 2 poin for drink size upgrade and 1 poin for extra shot",
 color = Color.White,
 fontSize = 11.sp
 )
 Spacer(modifier = Modifier.height(12.dp))
 Button(
 onClick = { },
 colors = ButtonDefaults.buttonColors(containerColor = Color.White),
 modifier = Modifier.height(32.dp),
 contentPadding = PaddingValues(horizontal = 16.dp, vertical = 4.dp)
 ) {
 Text(
 "SEE ALL OFFERS",
 color = Color(0xFF00704A),
 fontSize = 10.sp,
 fontWeight = FontWeight.Bold
 )
 }
 }
 Image(
 painter = painterResource(id = R.drawable.caramel_macchiato),
 contentDescription = "Happy Monday Drinks",
 modifier = Modifier
 .size(120.dp)
 .clip(RoundedCornerShape(12.dp)),
 contentScale = ContentScale.Crop
 )
 }
 }
 }

 item {
 // Good Morning Section
 Text(
 text = "Good Morning",
 style = MaterialTheme.typography.headlineSmall,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 4.dp)
 )
 Text(
 text = "How about a coffee break today?",
 style = MaterialTheme.typography.bodyMedium,
 color = Color.Gray,
 modifier = Modifier.padding(bottom = 16.dp)
 )
 }
 }

 item {
 // Search Bar
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(bottom = 16.dp),
 shape = RoundedCornerShape(12.dp),
 colors = CardDefaults.cardColors(containerColor = Color(0xFFF5F5F5))
 ) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp, vertical = 12.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 Icon(
 painter = painterResource(android.R.drawable.ic_menu_search),
 contentDescription = "Search",
 tint = Color.Gray,
 modifier = Modifier.size(20.dp)
 )
 Spacer(modifier = Modifier.width(12.dp))

 if (isSearchActive) {
 OutlinedTextField(
 value = searchQuery,
 onValueChange = { searchQuery = it },
 placeholder = { Text("Search Menu", color = Color.Gray) },
 colors = OutlinedTextFieldDefaults.colors(
 focusedBorderColor = Color.Transparent,
 unfocusedBorderColor = Color.Transparent,
 focusedContainerColor = Color.Transparent,
 unfocusedContainerColor = Color.Transparent
 ),
 modifier = Modifier.weight(1f),
 singleLine = true
 )

 IconButton(
 onClick = {
 isSearchActive = false
 searchQuery = ""
 }
 ) {
 Icon(
 imageVector = Icons.Default.Remove, // Using Remove as close icon
 contentDescription = "Close Search",
 tint = Color.Gray
 )
 }
 } else {
 Text(
 text = "Search Menu",
 color = Color.Gray,
 fontSize = 16.sp,
 modifier = Modifier
 .weight(1f)
 .clickable { isSearchActive = true }
 )
 }
 }
 }
 }

 if (isSearchActive) {
 // Search Results
 if (searchQuery.isNotBlank()) {
 item {
 Text(
 text = "Search Results (${filteredCoffeeDrinks.size})",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 12.dp)
 )
 }

 if (filteredCoffeeDrinks.isEmpty()) {
 item {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 40.dp),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Text(
 text = "No results found",
 style = MaterialTheme.typography.titleMedium,
 color = Color.Gray
 )
 Text(
 text = "Try searching for something else",
 style = MaterialTheme.typography.bodyMedium,
 color = Color.Gray
 )
 }
 }
 } else {
 items(filteredCoffeeDrinks) { drink ->
 SearchResultCard(drink)
 }
 }
 }
 } else {
 // Normal home content
 if (!isSearchActive) {
 item {
 // Card Balance
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(bottom = 24.dp),
 shape = RoundedCornerShape(12.dp),
 colors = CardDefaults.cardColors(containerColor = Color(0xFF00704A))
 ) {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 ) {
 Text(
 text = "Card Balance",
 color = Color.White,
 fontSize = 16.sp,
 fontWeight = FontWeight.Medium
 )
 Text(
 text = "Rp100.000",
 color = Color.White,
 fontSize = 28.sp,
 fontWeight = FontWeight.Bold
 )
 }
 }
 }

 item {
 // Today Favourite Section
 Text(
 text = "Today Favourite",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 12.dp)
 )

 LazyRow(
 horizontalArrangement = Arrangement.spacedBy(12.dp),
 modifier = Modifier.padding(bottom = 24.dp)
 ) {
 items(coffeeDrinks.take(3)) { drink ->
 HorizontalDrinkCard(drink)
 }
 }
 }

 item {
 // Popular Section
 Text(
 text = "Popular",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 12.dp)
 )

 LazyRow(
 horizontalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 items(coffeeDrinks) { drink ->
 HorizontalDrinkCard(drink)
 }
 }
 }
 }
 }
 }
}

