 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 4.dp),
 shape = RoundedCornerShape(12.dp),
 elevation = CardDefaults.cardElevation(defaultElevation = 2.dp),
 colors = CardDefaults.cardColors(containerColor = Color.White)
 ) {
 Row(
 modifier = Modifier
 .padding(16.dp)
 .fillMaxWidth(),
 verticalAlignment = Alignment.CenterVertically
 ) {
 // Item Image
 Card(
 shape = RoundedCornerShape(8.dp),
 modifier = Modifier.size(60.dp)
 ) {
 Image(
 painter = painterResource(id = item.imgRes),
 contentDescription = item.title,
 modifier = Modifier.fillMaxSize(),
 contentScale = ContentScale.Crop
 )
 }

 Spacer(modifier = Modifier.width(16.dp))

 // Item Details
 Column(modifier = Modifier.weight(1f)) {
 Text(
 text = item.title,
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold
 )
 Spacer(modifier = Modifier.height(4.dp))
 Text(
 text = item.details,
 style = MaterialTheme.typography.bodySmall,
 color = Color.Gray,
 maxLines = 2
 )
 Spacer(modifier = Modifier.height(8.dp))
 Text(
 text = "Rp${formatter.format((item.price * 1000).toInt())}",
 style = MaterialTheme.typography.bodyMedium,
 fontWeight = FontWeight.Bold,
 color = Color(0xFF00704A)
 )
 }

 Spacer(modifier = Modifier.width(16.dp))

 // Add to Cart Button
 Row(
 verticalAlignment = Alignment.CenterVertically
 ) {
 val itemInCart = cart.find { it.item.id == item.id }
 if (itemInCart != null && itemInCart.count > 0) {
 Text(
 text = itemInCart.count.toString(),
 fontSize = 14.sp,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(end = 8.dp)
 )
 }

 Card(
 modifier = Modifier
 .size(32.dp)
 .clickable {
 val existingItem = cart.find { it.item.id == item.id }
 if (existingItem != null) {
 existingItem.count++
 } else {
 cart.add(OrderEntry(item, 1))
 }
 },
 shape = RoundedCornerShape(8.dp),
 colors = CardDefaults.cardColors(containerColor = Color(0xFF00704A))
 ) {
 Box(
 modifier = Modifier.fillMaxSize(),
 contentAlignment = Alignment.Center
 ) {
 Icon(
 imageVector = Icons.Default.Add,
 contentDescription = "Add to Cart",
 tint = Color.White,
 modifier = Modifier.size(20.dp)
 )
 }
 }
 }
 }
 }
}

@Composable
fun CoffeeHouseApp() {
 var currentTab by remember { mutableStateOf(0) }
 val cart = remember { mutableStateListOf<OrderEntry>() }
 Scaffold(
 bottomBar = {
 NavigationBar {
 val tabs = listOf("Home", "Menu", "Order", "Account")
 val icons = listOf(
 Icons.Default.Home,
 Icons.Default.LocalCafe,
 Icons.Default.ShoppingCart,
 Icons.Default.Person
 )
 tabs.forEachIndexed { index, label ->
 NavigationBarItem(
 selected = currentTab == index,
 onClick = { currentTab = index },
 icon = { Icon(icons[index], contentDescription = label) },
 label = { Text(label) }
 )
 }
 }
 }
 ) { innerPadding ->
 Box(modifier = Modifier.padding(innerPadding)) {
 when (currentTab) {
 0 -> WelcomeScreen()
 1 -> DrinkMenuScreen(cart)
 2 -> CartPage(cart)
 3 -> UserProfileScreen()
 }
 }
 }
}

